components/gordonbyte/common/video/video:
{{#if theme_settings.gb-featured-video-enable }}
  <div class="featured-video product-sections">

    <div class="featured-holder">
      <div class="video-controls">
        <a href="{{ theme_settings.gb-featured-video-url }}" class="button btn button--primary style-action" aria-label="video play button" data-lity>
          <svg height="20px" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 31.02 38.02">
            <path d="M31,19,0,38V0Z" transform="translate(0.01 0.01)"/>
          </svg>
        </a>
      </div><!--.video-controls ends-->
      <h2 class="page-heading"><span>{{ theme_settings.gb-featured-video-header-text }}</span></h2>
    </div>

    <div class="featured-video-img{{#unless theme_settings.gb-featured-video-thumbnail }}{{#unless theme_settings.gb-theme-demo }} no-img-size{{/unless}}{{/unless}}">
      {{#if theme_settings.gb-featured-video-thumbnail }}
      <img src="{{getImageManagerImage theme_settings.gb-featured-video-thumbnail }}"
            data-sizes="auto"
            alt="{{ theme_settings.gb-featured-video-thumbnail }}"
            {{#unless lazyload '==' 'disabled'}}
                {{#if lazyload '==' 'lazyload+lqip'}}
                {{!-- If we want a LQIP, include it as the default srcset (will be overwritten by lazysizes) --}}
                srcset="{{getImageManagerImageSrcset theme_settings.gb-featured-video-thumbnail }}"
                {{else}}
                  {{!-- If we don't want a LQIP, use a 1px transparent gif for the stand-in image
                        This requires no additional network request, so it's fast --}}
                srcset="data:image/gif;base64,R0lGODlhAQABAIAAAP///wAAACH5BAEAAAAALAAAAAABAAEAAAICRAEAOw=="
                {{/if}}
            {{/unless}}

            {{#unless lazyload '==' 'disabled'}}data-{{/unless}}srcset="{{getImageManagerImageSrcset theme_settings.gb-featured-video-thumbnail use_default_sizes=true }}"

            class="{{#unless lazyload '==' 'disabled'}}lazyload{{/unless}}"
            {{#if lazyload '==' 'lazyload'}}loading="lazy"{{/if}}
            >
      {{else}}
        {{#if theme_settings.gb-theme-demo }}
          {{> components/gordonbyte/demo/common/img path=theme_settings.gb-theme-name file_name='-featured-video.jpg' }}
        {{else}}
          {{> components/gordonbyte/common/no-img }}
        {{/if}}
      {{/if}}
    </div>


  </div><!--.featured-video ends-->
{{/if}}
