components/gordonbyte/common/blog-articl:
{{#each (limit blog.recent_posts posts_count)}}
<div class="blog-content-holder ">
  <a href="{{url}}">
  <div class="latest-blog-content">
    <div class="latest-blog-img{{#unless thumbnail }} no-img-size{{/unless}}">
      {{#if thumbnail }}
      <img src="{{getImage thumbnail }}"
            data-sizes="auto"
            alt="{{title}}"
            {{#unless lazyload '==' 'disabled'}}
                {{#if lazyload '==' 'lazyload+lqip'}}
                {{!-- If we want a LQIP, include it as the default srcset (will be overwritten by lazysizes) --}}
                srcset="{{getImageSrcset thumbnail}}"
                {{else}}
                  {{!-- If we don't want a LQIP, use a 1px transparent gif for the stand-in image
                        This requires no additional network request, so it's fast --}}
                srcset="data:image/gif;base64,R0lGODlhAQABAIAAAP///wAAACH5BAEAAAAALAAAAAABAAEAAAICRAEAOw=="
                {{/if}}
            {{/unless}}

            {{#unless lazyload '==' 'disabled'}}data-{{/unless}}srcset="{{getImageSrcset thumbnail use_default_sizes=true }}"

            class="{{#unless lazyload '==' 'disabled'}}lazyload{{/unless}}"
            {{#if lazyload '==' 'lazyload'}}loading="lazy"{{/if}}
            >
    {{else}}
      {{> components/gordonbyte/common/no-img}}
    {{/if}}
    </div>
    <div class="latest-blog-title">
      <h3>{{title}}</h3>
      <div class="blogInfo">
        {{summary}}
      </div><!--.blogInfo ends-->
      <div class="latest-read-more">
        Read More
      </div>
    </div>
  </div><!--.latest-blog-content ends-->
  </a>
</div><!--.blog-content-holder ends-->
{{/each}}

components/gordonbyte/footer:
{{#if banners.bottom}}
    {{#if settings.data_tag_enabled}}
        <div class="banners" data-banner-location="bottom">
            {{#each (limit banners.bottom_metadata 1)}}
                <div class="banner" data-event-type="promotion" data-entity-id="{{this.id}}" data-name="{{this.banner-name}}" data-position="{{this.location}}" data-name="{{this.banner-name}}">
                    <div data-event-type="promotion-click">
                        {{{this.content}}}
                    </div>
                </div>
            {{/each}}
        </div>
    {{else}}
        <div class="banners" data-banner-location="bottom">
            <div class="banner">
                {{{limit banners.bottom 1}}}
            </div>
        </div>
    {{/if}}

{{/if}}


<footer class="footer top-new" role="contentinfo">
    <h2 class="footer-title-sr-only">{{lang 'footer.title'}}</h2>
    <div class="container">
        <section class="footer-info{{#if theme_settings.gb-footer-style '==' 3}} footer_three{{/if}}">

            <article class="footer-info-col footer-info-holder" data-section-type="storeInfo">
                <div class="footer-logo">
                  {{#if page_type '===' 'default'}}
                      <h1 class="header-logo header-logo--{{theme_settings.logo-position}}">
                          {{> components/gordonbyte/common/store-logo-footer }}
                      </h1>
                  {{else}}
                      <div class="header-logo header-logo--{{theme_settings.logo-position}}">
                          {{> components/gordonbyte/common/store-logo-footer }}
                      </div>
                  {{/if}}
                </div>

                {{#if settings.address}}
                  <address>{{nl2br settings.address}}</address>
                {{/if}}

                {{#if settings.phone_number}}
                    <strong>{{lang 'footer.call_us' phone_number=settings.phone_number}}</strong>
                {{/if}}

                {{> components/gordonbyte/common/social-links}}
            </article>

            <article class="footer-info-col footer-link" data-section-type="footer-webPages">
                <h2 class="footer-info-heading">{{lang 'footer.navigate'}}</h2>
                <ul class="footer-info-list">
                    {{#each (limit pages (subtract theme_settings.gb-footer-columnLinks 1))}}
                        <li>
                            <a href="{{url}}">{{name}}</a>
                        </li>
                    {{/each}}
                    <li>
                        <a href="{{urls.sitemap}}">{{lang 'sitemap.page_title'}}</a>
                    </li>
                </ul>
            </article>

            <article class="footer-info-col footer-link" data-section-type="footer-categories">
                <h2 class="footer-info-heading">{{lang 'footer.categories'}}</h2>
                <ul class="footer-info-list">
                    {{#each (limit categories theme_settings.gb-footer-columnLinks)}}
                        <li>
                            <a href="{{url}}">{{name}}</a>
                        </li>
                    {{/each}}
                </ul>
            </article>


            <article class="footer-info-col footer-link" data-section-type="footer-brands">
              {{#and theme_settings.shop_by_brand_show_footer shop_by_brand.length}}
                <h2 class="footer-info-heading">{{lang 'footer.brands'}}</h2>
                <ul class="footer-info-list">
                    {{#each (limit shop_by_brand (subtract theme_settings.gb-footer-columnLinks 1))}}
                        <li>
                            <a href="{{url}}">{{name}}</a>
                        </li>
                    {{/each}}
                    <li><a href="{{urls.brands}}">{{lang 'common.view_all'}}</a></li>
                </ul>
                {{/and}}
            </article>

            {{#if theme_settings.gb-footer-style "==" 1}}
            <article class="footer-info-col footer-news" data-section-type="newsletterSubscription">
                {{#if settings.show_newsletter_box}}
                    {{> components/gordonbyte/common/newsletter/subscription-form }}
                {{/if}}
            </article>
            {{/if}}

            {{#if theme_settings.gb-footer-style "==" 2}}
                <div class="footer-blog">
                  <h2 class="footer-info-heading">{{ theme_settings.gb-blog-main-title }}</h2>
                  {{> components/gordonbyte/common/blog-article posts_count=1}}
                </div>
            {{/if}}

        </section>

        <div class="footer-geotrust-ssl-seal">
            {{{region name="ssl_site_seal--global"}}}
        </div>

    </div>

        <div class="footer-copyright">
            <div class="container">

              <div class="copyright-holder">
                <div class="copyright-right">
                    {{> components/common/payment-icons}}
                </div>

                <div class="copyright-left">
                  {{#if theme_settings.show_copyright_footer}}
                          <span class="powered-by">&copy; {{moment format="YYYY"}} {{settings.store_name}} </span>
                  {{/if}}
                  {{#if theme_settings.show_powered_by}}
                          <span class="powered-by">{{lang 'footer.powered_by'}} <a href="https://www.bigcommerce.com?utm_source=merchant&amp;utm_medium=poweredbyBC" rel="nofollow">BigCommerce</a></span>
                  {{/if}}
                  {{#if settings.paypal_commerce_credit_message}}
                      <span class="paypal-credit">{{nl2br settings.paypal_commerce_credit_message}}</span>
                  {{/if}}
                </div>
              </div><!--.copyright-holder ends-->

            </div>
        </div>
</footer>

{{{region name="home_below_footer"}}}
{{{region name="home_below_footer--global"}}}
