<div class="categoryProducts product-sections" {{#if theme_settings.gb_default_background }}style="background-image:url({{cdn 'img/bg-img.jpg'}});" {{/if}}>
  <div class="container">
    <h2 class="page-heading"><span>{{ theme_settings.gb-category-product-section-main-heading }}</span></h2>

      <div>{{{region name="home_our_product_description"}}}</div>

    <div class="category-selections" data-gbperrow="{{ theme_settings.gb-category-product-section-per-row }}" data-gbloadin="{{ theme_settings.gb-category-product-section-load-in }}">
      <ul>
        {{#if theme_settings.gb-category-product-section-one-name }}
        <li class="active-cat" data-gbtype="{{ theme_settings.gb-category-product-section-one-type }}" data-gbcatid="{{ theme_settings.gb-category-product-section-one-name }}">
          <a href="#">

            {{#if theme_settings.gb-category-product-section-enable-imgs }}
            <div class="caticon">
              {{#if theme_settings.gb-category-product-section-one-img }}
                <img src="{{getImageManagerImage theme_settings.gb-category-product-section-one-img }}"
                      data-sizes="auto"
                      alt="{{ theme_settings.gb-category-product-section-one-img }}"
                      {{#unless lazyload '==' 'disabled'}}
                          {{#if lazyload '==' 'lazyload+lqip'}}
                          {{!-- If we want a LQIP, include it as the default srcset (will be overwritten by lazysizes) --}}
                          srcset="{{getImageManagerImageSrcset theme_settings.gb-category-product-section-one-img }}"
                          {{else}}
                            {{!-- If we don't want a LQIP, use a 1px transparent gif for the stand-in image
                                  This requires no additional network request, so it's fast --}}
                          srcset="data:image/gif;base64,R0lGODlhAQABAIAAAP///wAAACH5BAEAAAAALAAAAAABAAEAAAICRAEAOw=="
                          {{/if}}
                      {{/unless}}

                      {{#unless lazyload '==' 'disabled'}}data-{{/unless}}srcset="{{getImageManagerImageSrcset theme_settings.gb-category-product-section-one-img use_default_sizes=true }}"

                      class="{{#unless lazyload '==' 'disabled'}}lazyload{{/unless}}"
                      {{#if lazyload '==' 'lazyload'}}loading="lazy"{{/if}}
                      >
              {{else}}
                {{#if theme_settings.gb-theme-demo }}
                  {{> components/gordonbyte/demo/common/img path=theme_settings.gb-theme-name file_name='1category-products.png' }}
                {{/if}}
              {{/if}}
            </div>
            {{/if}}

            {{ theme_settings.gb-category-product-section-one-title }}
          </a>
        </li>
        {{/if}}

