components/gordonbyte/common/featured-in:
{{#if theme_settings.gb-featured-info-description-position '==' 'left' }}
  {{assignVar 'animation_direction' 'right'}}
{{else}}
  {{assignVar 'animation_direction' 'left'}}
{{/if}}

{{#if theme_settings.gb-featured-info-enable }}
<div class="featured-info{{#if theme_settings.gb-featured-info-description-position }} {{theme_settings.gb-featured-info-description-position}}-featureinfo{{/if}}">

  <div class="featured-info-img{{#unless theme_settings.gb-featured-info-background-image }}{{#unless theme_settings.gb-theme-demo }} no-img-size{{/unless}}{{/unless}}">
    {{#if theme_settings.gb-featured-info-background-image }}
    <img src="{{getImageManagerImage theme_settings.gb-featured-info-background-image }}"
          data-sizes="auto"
          alt="{{ theme_settings.gb-featured-info-background-image }}"
          {{#unless lazyload '==' 'disabled'}}
              {{#if lazyload '==' 'lazyload+lqip'}}
              {{!-- If we want a LQIP, include it as the default srcset (will be overwritten by lazysizes) --}}
              srcset="{{getImageManagerImageSrcset theme_settings.gb-featured-info-background-image }}"
              {{else}}
                {{!-- If we don't want a LQIP, use a 1px transparent gif for the stand-in image
                      This requires no additional network request, so it's fast --}}
              srcset="data:image/gif;base64,R0lGODlhAQABAIAAAP///wAAACH5BAEAAAAALAAAAAABAAEAAAICRAEAOw=="
              {{/if}}
          {{/unless}}

          {{#unless lazyload '==' 'disabled'}}data-{{/unless}}srcset="{{getImageManagerImageSrcset theme_settings.gb-featured-info-background-image use_default_sizes=true }}"

          class="{{#unless lazyload '==' 'disabled'}}lazyload{{/unless}}"
          {{#if lazyload '==' 'lazyload'}}loading="lazy"{{/if}}
          >
    {{else}}
      {{#if theme_settings.gb-theme-demo }}
        {{> components/gordonbyte/demo/common/img path=theme_settings.gb-theme-name file_name='-featured-info.jpg' }}
      {{else}}
        {{> components/gordonbyte/common/no-img }}
      {{/if}}
    {{/if}}
  </div>

    <div class="featured-info-position">
      <div class="featured-info-holder">
        {{#if theme_settings.gb-featured-info-title }}
          <h2 class="page-heading"><span>{{ theme_settings.gb-featured-info-title }}</span></h2>
        {{/if}}

          {{{region name="home_feature_info_content"}}}

        {{#if theme_settings.gb-featured-info-cta-btn-text }}
          <div class="view-all-btn">
            <a href="{{ theme_settings.gb-featured-info-cta-btn-url }}" class="button btn button--primary style-action"><span>{{ theme_settings.gb-featured-info-cta-btn-text }}</span></a>
          </div>
        {{/if}}
      </div>
    </div>
</div><!--.featured-info ends-->
{{/if}}
